operations.reducer:
state.id = action.input.id;
state.name = action.input.name;
state.description = action.input.description || null;
state.createdAt = action.input.createdAt;

operations.schema:
input CreateDaoInput {
  id: OID!
  name: String!
  description: String
  createdAt: DateTime!
}

input UpdateDaoMetadataInput {
  name: String
  description: String
}
